import React, { useState, useEffect } from 'react';

const Header = () => {
  const [searchActive, setSearchActive] = useState(false);
  const [headerActive, setHeaderActive] = useState(false);

  useEffect(() => {
    const onScroll = () => {
      setSearchActive(false);
      if (window.scrollY > 0) {
        setHeaderActive(true);
      } else {
        setHeaderActive(false);
      }
    };
    window.addEventListener('scroll', onScroll);
    if (window.scrollY > 80) {
      setHeaderActive(true);
    }
    return () => window.removeEventListener('scroll', onScroll);
  }, []);

  return (
    <header className="header">
      <div className="header-1">
        <a href="#" className="logo"><i className="fas fa-paw"></i> PETSHOP</a>

        <form action="" className={`search-form${searchActive ? ' active' : ''}`}>
          <input type="search" name="" placeholder="Search here..." id="search-box" />
          <label htmlFor="search-box" className="fas fa-search"></label>
        </form>

        <div className="icons">
          <div id="search-btn" className="fas fa-search" onClick={() => setSearchActive(!searchActive)}></div>
          <a href="#" className="fas fa-heart"></a>
          <a href="#" className="fas fa-shopping-cart"></a>
          <div id="login-btn" className="fas fa-user"></div>
        </div>
      </div>

      <div className={`header-2${headerActive ? ' active' : ''}`}>
        <nav className="navbar">
          <a href="#home">home</a>
          <a href="#featured">featured</a>
          <a href="#product">product</a>
          <a href="#reviews">reviews</a>
          <a href="#blog">blog</a>
        </nav>
      </div>
    </header>
  );
};

export default Header
