import React, { useState, useEffect } from 'react';
import './ProductCatalog.css';

const ProductCatalog = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [filters, setFilters] = useState({
    priceRange: [0, 1000],
    brand: '',
    rating: '',
    inStock: false,
  });
  const [sortBy, setSortBy] = useState('newest');

  // Dữ liệu mẫu với thương hiệu chỉ có Chó và Mèo
  useEffect(() => {
    const sampleProducts = [
      { id: 1, name: 'Thức ăn chó', category: 'Thức ăn', brand: 'Chó', price: 200, rating: 4.5, inStock: true, dateAdded: '2025-04-15', image: '/image/meobatu.jpg' },
      { id: 2, name: 'Đồ chơi mèo', category: 'Đồ chơi', brand: 'Mèo', price: 150, rating: 4.0, inStock: false, dateAdded: '2025-04-20', image: '/image/n2.jpg' },
      { id: 3, name: 'Lồng chim', category: 'Phụ kiện', brand: 'Chó', price: 300, rating: 4.8, inStock: true, dateAdded: '2025-03-10', image: '/image/meolongdai.jpg' },
      { id: 4, name: 'Dây dắt chó', category: 'Phụ kiện', brand: 'Chó', price: 100, rating: 4.2, inStock: true, dateAdded: '2025-04-01', image: '/image/doodle.jpg' },
      { id: 5, name: 'Thức ăn mèo', category: 'Thức ăn', brand: 'Mèo', price: 250, rating: 4.7, inStock: true, dateAdded: '2025-04-18', image: '/image/sale.jpg' },
    ];
    setProducts(sampleProducts);
    applyFiltersAndSort(sampleProducts);
  }, []);

  const applyFiltersAndSort = (productsToFilter) => {
    let result = [...productsToFilter];

    // Bộ lọc
    if (filters.inStock) result = result.filter(p => p.inStock);
    if (filters.brand) result = result.filter(p => p.brand === filters.brand);
    if (filters.rating) result = result.filter(p => p.rating >= parseFloat(filters.rating));
    result = result.filter(p => p.price >= filters.priceRange[0] && p.price <= filters.priceRange[1]);

    // Sắp xếp
    switch (sortBy) {
      case 'newest':
        result.sort((a, b) => new Date(b.dateAdded) - new Date(a.dateAdded));
        break;
      case 'bestseller':
        result.sort((a, b) => b.rating - a.rating);
        break;
      case 'priceAsc':
        result.sort((a, b) => a.price - b.price);
        break;
      case 'priceDesc':
        result.sort((a, b) => b.price - a.price);
        break;
      default:
        break;
    }

    setFilteredProducts(result);
  };

  const handleFilterChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value === 'all' ? '' : value,
    }));
  };

  const handleSortChange = (e) => {
    setSortBy(e.target.value);
  };

  useEffect(() => {
    applyFiltersAndSort(products);
  }, [filters, sortBy, products]);

  return (
    <div className="product-catalog">
      <h2>Danh mục sản phẩm</h2>

      {/* Bộ lọc */}
      <div className="filters">
        <label>Giá (VNĐ): 
          <input type="range" name="priceRange" min="0" max="1000" value={filters.priceRange[1]} onChange={(e) => setFilters(prev => ({ ...prev, priceRange: [0, parseInt(e.target.value)] }))} />
          {filters.priceRange[0]} - {filters.priceRange[1]}
        </label>
        <label>Thương hiệu: 
          <select name="brand" value={filters.brand} onChange={handleFilterChange}>
            <option value="all">Tất cả</option>
            <option value="Chó">Chó</option>
            <option value="Mèo">Mèo</option>
          </select>
        </label>
        <label>Đánh giá: 
          <select name="rating" value={filters.rating} onChange={handleFilterChange}>
            <option value="all">Tất cả</option>
            <option value="4">4 sao trở lên</option>
            <option value="4.5">4.5 sao trở lên</option>
          </select>
        </label>
        <label>Còn hàng: 
          <input type="checkbox" name="inStock" checked={filters.inStock} onChange={handleFilterChange} />
        </label>
      </div>

      {/* Sắp xếp */}
      <div className="sort">
        <label>Sắp xếp theo: 
          <select value={sortBy} onChange={handleSortChange}>
            <option value="newest">Mới nhất</option>
            <option value="bestseller">Bán chạy</option>
            <option value="priceAsc">Giá: Thấp đến Cao</option>
            <option value="priceDesc">Giá: Cao đến Thấp</option>
          </select>
        </label>
      </div>

      {/* Danh sách sản phẩm */}
      <div className="product-list">
        {filteredProducts.map(product => (
          <div key={product.id} className="box">
            <div className="icons">
              <a href="#" className="fas fa-search"></a>
              <a href="#" className="fas fa-heart"></a>
              <a href="#" className="fas fa-eye"></a>
            </div>
            <div className="image">
              <img src={product.image} alt={product.name} />
            </div>
            <div className="content">
              <h3>{product.name}</h3>
              <p>Danh mục: {product.category}</p>
              <p>Thương hiệu: {product.brand}</p>
              <div className="price">{product.price} VNĐ</div>
              <p>Đánh giá: {product.rating} ★</p>
              <p>Còn hàng: {product.inStock ? 'Có' : 'Hết'}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductCatalog;
