import React, { useState } from 'react';

function LoginForm() {
  const [visible, setVisible] = useState(false);

  const closeForm = () => {
    setVisible(false);
  };

  // For demonstration, form is visible by default.
  // You can lift state up to App or Header for toggle control.

  return (
    <div className={`login-form-container${visible ? ' active' : ''}`}>
      <div id="close-login-btn" className="fas fa-times" onClick={closeForm}></div>

      <form action="/login">
        <h3>Sign In</h3>

        <span>Username</span>
        <input type="email" name="email" className="box" placeholder="Enter your email" id="email" />

        <span>Password</span>
        <input type="password" name="password" className="box" placeholder="Enter your password" id="password" />

        <div className="checkbox">
          <input type="checkbox" name="remember" id="remember-me" />
          <label htmlFor="remember-me">Remember me</label>
        </div>

        <input type="submit" value="Sign in" className="btn" />

        <p>Forgot password? <a href="#">Click here</a></p>
        <p>Don't have an account? <a href="#">Create one</a></p>
      </form>
    </div>
  );
}

export default LoginForm;

/*
Giải thích:
- Sử dụng function component với state visible để điều khiển hiển thị form.
- Thêm hàm closeForm để ẩn form khi click nút đóng.
- Cần nâng state này lên cha hoặc dùng context để Header có thể bật/tắt form khi click icon user.
- Giữ nguyên cấu trúc và className để áp dụng CSS.
*/
