const Deal = () => {
  return (
    <section className="deal">
      <div className="content">
        <h3>giao dịch trong ngày</h3>
        <h1>lên tới 70%</h1>
        <p>hãy ghé đến shop chúng tôi</p>
        <a href="#" className="btn">mua ngay</a>
      </div>

      <div className="image">
        <img src="image/0.jpg" alt="" />
      </div>
    </section>
  );
};

export default Deal;

