import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const sampleProducts = [
  { id: 1, name: 'Thức ăn chó', category: 'Thức ăn', brand: 'Chó', price: 200, rating: 4.5, inStock: true, dateAdded: '2025-04-15', image: '/image/meobatu.jpg', description: 'Thức ăn dinh dưỡng cho chó, giúp chó khỏe mạnh và năng động.' },
  { id: 2, name: 'Đồ chơi mèo', category: 'Đồ chơi', brand: 'Mèo', price: 150, rating: 4.0, inStock: false, dateAdded: '2025-04-20', image: '/image/n2.jpg', description: 'Đồ chơi an toàn và thú vị cho mèo cưng của bạn.' },
  { id: 3, name: 'Lồng chim', category: 'Phụ kiện', brand: 'Chó', price: 300, rating: 4.8, inStock: true, dateAdded: '2025-03-10', image: '/image/meolongdai.jpg', description: 'Lồng chim chắc chắn, thoáng mát, dễ dàng vệ sinh.' },
  { id: 4, name: 'Dây dắt chó', category: 'Phụ kiện', brand: 'Chó', price: 100, rating: 4.2, inStock: true, dateAdded: '2025-04-01', image: '/image/doodle.jpg', description: 'Dây dắt chắc chắn, thoải mái cho chó khi đi dạo.' },
  { id: 5, name: 'Thức ăn mèo', category: 'Thức ăn', brand: 'Mèo', price: 250, rating: 4.7, inStock: true, dateAdded: '2025-04-18', image: '/image/sale.jpg', description: 'Thức ăn dinh dưỡng cho mèo, giúp mèo phát triển khỏe mạnh.' },
];

const sampleReviews = [
  { id: 1, user: 'Nguyễn Văn A', comment: 'Sản phẩm rất tốt, thú cưng của tôi rất thích.', rating: 5 },
  { id: 2, user: 'Trần Thị B', comment: 'Chất lượng ổn, giao hàng nhanh.', rating: 4 },
];

const ProductDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const product = sampleProducts.find(p => p.id === parseInt(id));

  if (!product) {
    return <div>Không tìm thấy sản phẩm.</div>;
  }

  const handleAddToCart = () => {
    alert('Đã thêm sản phẩm vào giỏ hàng!');
  };

  const handleBuyNow = () => {
    alert('Chức năng mua ngay chưa được triển khai.');
  };

  return (
    <div className="product-detail">
      <button onClick={() => navigate(-1)}>Quay lại</button>
      <h2>{product.name}</h2>
      <img src={product.image} alt={product.name} style={{ maxWidth: '300px' }} />
      <p>Giá: {product.price} VNĐ</p>
      <p>Mô tả: {product.description}</p>
      <p>Đánh giá: {product.rating} ★</p>
      <p>Tình trạng: {product.inStock ? 'Còn hàng' : 'Hết hàng'}</p>

      <div className="reviews">
        <h3>Đánh giá và bình luận</h3>
        {sampleReviews.map(review => (
          <div key={review.id} className="review">
            <strong>{review.user}</strong>: {review.comment} ({review.rating} ★)
          </div>
        ))}
      </div>

      <button onClick={handleAddToCart}>Thêm vào giỏ hàng</button>
      <button onClick={handleBuyNow}>Mua ngay</button>
    </div>
  );
};

export default ProductDetail;
